import React from 'react'

export default function CreateWine() {
    const [formData, setFormData] = useState({
        vintage: 0,
        name: '',
        varietal: '',
        price_min: 0,
        price_max: 0,
        size: 0,
    })

    const { name } = formData;
    const { handleCreate } = props;

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }))
    }

    return (
        <form onSubmit={(e)=> {
            e.preventDefault();
            handleCreate(formData)
        }}>
        <TextField
          required
          onChange={handleChange}
          name="itemName"
          value={itemName}
          className="textfield"
          label="Item Title"
          variant="outlined"
          type="text"
        />
        <TextField
          required
          onChange={handleChange}
          name="imgURL"
          value={imgURL}
          className="textfield"
          label="image URL"
          variant="outlined"
          type="text"
        />
        <TextField
          required
          multiline
          onChange={handleChange}
          name="description"
          value={description}
          className="textfield"
          label="Description"
          rows={4}
          variant="outlined"
        />
        <TextField
          required
          onChange={handleChange}
          name="price"
          value={price}
          className="textfield"
          label="Price"
          variant="outlined"
          type="text"
          startAdornment={<InputAdornment position="start">$</InputAdornment>}
        />
        <TextField
          required
          select
          onChange={handleChange}
          name="itemType"
          value={itemType}
          className="textfield"
          label="Item Type "
          variant="outlined"
          type="text"
        />
        /></form>
    )
}
